INSERT INTO `tbl_opinions` (`id`, `name`, `text`, `date`, `is_view`, `is_new`) VALUES
(3, 'Андрей', 'Заказывал композицию из орхидей в подарок супруге - все в срок и очень красиво. Еще плюс - это огромный выбор, и даже если нет нужного цветка в наличии, всегда есть возможность сделать заказ на любую представленную орхидею.', '2013-09-13', 1, 0),
(2, 'Настя', 'Ребята, вы просто супер!! Таких свежих и стойких орхидей никогда не видела! Заказывала белый фаленопсис с тремя цветоносами 2,5 месяца назад - цветы до сих пор стоят, как будто я ее только что купила! Спасибо!', '2013-09-13', 1, 0),
(4, 'Марина', 'Хороший ассортимент, но нет редких видов в наличии, только под заказ. Заказывала желтую Ванду с доставкой в Брест, в течение двух дней цветок был доставлен в целостности и сохранности.', '2013-09-13', 1, 0),
(5, 'Ольга Новосельцева', 'Все понравилось, цветы действительно очень свежие и нет никаких проблем с корнями, особенно сравнивая с тем, что сейчас предлагают в магазинах. Плюс цена - цена в среднем ниже на 15-25% и плюс бесплатная доставка тоже приятно радует. Из минусов это отсутствие всяческих принадлежностей для орхидей - почему бы Вам не продавать грунт для орхидей, цветочные горшки и специальные подставки и вазы? Этим бы вы сделали себя еще лучше и избавили покупателей от поиска этих атрибутов в других магазинах. Спасибо', '2013-09-13', 1, 0),
(6, 'Валентина ', 'Очень понравился подход флориста, - никто не навязывает свое мнение, а прежде всего выслушали и узнали, что конкретно мне нужно. Покупала здесь синюю Ванду в стекле для интерьера в квартире, брала по цене на скидке, вместо 1.200.000 цена была 990.000 - качество выше всяких похвал. Приятный курьер и бесплатная доставка. Спасибо, все очень понравилось!', '2013-09-13', 1, 0),
(7, 'Антонина Михайловна', 'Я, как большая любительница и коллекционер орхидей осталась очень довольна великолепным выбором и консультацией этого салона. Не буду хвастаться, сколько в моей коллекции уже есть орхидей, скажу только, что по ассортименту, а может даже по количеству гораздо больше, чем в некоторых представленных цветочных магазинах. Только самого простого вида этого цветка, - фаленопсиса, в моей коллекции собрано более 25 разных видов и расцветок, не говоря уже о белее редких видах, которые так просто в магазинах не найти. Заказывала на сайте любимую мной, нежно фиолетовую Камбрию, которая сейчас меня очень радует своим приятным цветением.', '2013-09-13', 1, 0),
(8, 'Дарья', 'Не люблю писать отзывы, к сожалению, очень часто написать можно только не очень хорошее в адрес продавцов. Но здесь не могу не поблагодарить. Спасибо Вам огромное за особый подход к пожеланию Заказчика; за умение слЫшать и старание помочь найти именно то, что хочет Клиент, а если этого нет, то предложить еще лучше, чем хотел человек; за то, что НЕ обманываете!!! Покупала композицию на ДР другу, тот прям прослезился - счас не налюбуется!!! СПАСИБО!!!!', '2014-01-26', 0, 0),
(21, 'Ольга', 'Заказывали 2 орхидеи - мини и стандарт. На картинке мини выглядит совершенно по-другому - большие цветы. В реале оказались маленькие совершенно. Такое впечатление, что 2 разных сорта. Разочарована.\r\nК стандартной претензий нет.', '2015-07-01', 1, 0),
(22, 'Светлана и  Андреас, Австрия', 'Уважаемая Маргарита, мы очень благодарны за то, что Вы от нашего имени поздравили жениха и невесту и вручили им замечательные орхидеи.', '2015-08-10', 1, 0),
(17, 'ирина', 'вы супер', '2015-02-04', 1, 0),
(20, 'Елена', 'Вчера с подругой заезжали за отцветшими орхидеями. Остались очень довольны. Цветочки с хорошей корневой системой. Очень приятная и вежливая в общении девушка помогала в выборе)) Спасибо за вашу акцию!!!', '2015-05-28', 1, 0),
(13, 'Валерия', 'Всё на высшем уровне! Обслуживание и пунктуальность очень порадовали!!! Заказывала дендробиум,Цветы шикарные,свежие!!!Осталась в восторге, буду обращаться ещё и ещё.', '2014-09-24', 0, 1),
(23, 'Александр', 'Просто СУПЕРРРР Сервис. И я не шучу. Надо ? Получите. Быстро . Аккуратно и Очень Вежливо. Суперрр. Только к Вам.\r\nСпасибо за помощь.', '2015-08-17', 1, 0),
(24, 'Лана', 'Почему вы такую цену ломите за крашенный фаленопсис?', '2015-08-17', 0, 0),
(25, 'Инна', 'Моя орхидейка просто очаровательна!!! Спасибо огромное! Действительно Вам благодарна. Подход консультанта не просто индивидуален, я бы сказала "с душой". ', '2015-08-23', 1, 0),
(26, 'Елена', 'Не знаю, за что расхваливают этот магазин. Работа сотрудников, мягко говоря, несколько удивляет. Сделала заказ вечером на следующий день - хотели поздравить учителя в школе. Никто не перезвонил и, соответственно, ничего не доставил.Перезвонила сама. Пообещали сфотографировать цветы и сбросить фото на вайбер. Четвертый час жду фотографий. Вот так и портят людям праздники. Не рекомендую. И на отзывах.бай так и напишу.', '2015-09-22', 0, 1),
(27, 'Юлия', 'Замечательный магазин. Все оперативно. Вежливые и знающие своё дело консультанты (большая редкость в наше время). Большое спасибо. Буду рекомендовать знакомым.', '2015-10-14', 0, 1);
